const burger=document.querySelector(".burger"),menu=document.querySelector(".nav"),overlay=document.querySelector(".header-overlay"),body=document.querySelector("body");function toggle(){menu.classList.toggle("show"),burger.classList.toggle("show"),overlay.classList.toggle("show"),body.classList.toggle("stop-scroll")}burger.addEventListener("click",()=>{toggle()}),body.addEventListener("click",e=>{e.target.classList.contains("header-overlay")&&toggle()});